import Navbar from "../components/Navbar";
import CartBookList from "../components/CartBookList";
import Countbar from "../components/Countbar";
import {useEffect, useState} from "react";
import coverImageUrl from "../img/bg.jpg";
export default function CartPage(){
    const [cartbook,setCartbook] =useState( [
        { id: 1, title: "Book One", author: "Author One", coverImageUrl: coverImageUrl, littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100, quantity: 1,checked: false},
        { id: 2, title: "Book Two", author: "Author Two", coverImageUrl: coverImageUrl, littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100,quantity: 1,checked: false},
        { id: 3, title: "Book Three", author: "Author Three", coverImageUrl: coverImageUrl , littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100,quantity: 1,checked: false},
        { id: 4, title: "Book 4", author: "Author Three", coverImageUrl: coverImageUrl , littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100,quantity: 1,checked: false},
        { id: 5, title: "Book 5", author: "Author Three", coverImageUrl: coverImageUrl , littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100,quantity: 1,checked: false},
        { id: 6, title: "Book 6", author: "Author Three", coverImageUrl: coverImageUrl , littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100,quantity: 1,checked: false},
        { id: 7, title: "Book 7", author: "Author Three", coverImageUrl: coverImageUrl , littleIntroduction: "This is a book about...", Introduction: "This is a book about...", price: 100,quantity: 1,checked: false}
        // 更多书籍...
    ]);

    const [totalPrice,setTotalPrice] = useState(0);
    const [totalNum,setTotalNum]=useState(0);
    const handleCheck = (id,isChecked) => {
        const newCartbook = cartbook.map(book =>
            book.id === id ? { ...book, checked: isChecked } : book
        );
        setCartbook(newCartbook);
    }
    const handleQuantity = (id,quantity) => {
        const newCartbook = cartbook.map(book =>
            book.id === id ? { ...book, quantity: quantity } : book
        );
        setCartbook(newCartbook);
    }

    const handleSelectAll =(isSelectAll)=>{
        const newCartbook = cartbook.map(book =>({
                ...book,
                checked: isSelectAll }
        ));
        setCartbook(newCartbook);
    }

    useEffect(() => {
        // 这个函数会在cartbook状态更新后调用
        handleCount();
    }, [cartbook]); // 依赖数组里面包含了cartbook，意味着cartbook变化时会触发这个effect

    const handleCount = () => {
        let totalprice = 0;
        let totalnum=0;
        cartbook.map(book => {
            if (book.checked) {
                totalprice += parseFloat(book.price) * parseInt(book.quantity);
                totalnum+=parseInt(book.quantity);
            }
        });
        setTotalPrice(totalprice);
        setTotalNum(totalnum)
    }

    return (
        <div>
            <div className="absolute w-full top-24 px-16  bg-gray-100">
                <CartBookList cartbook={cartbook} handleCheck={handleCheck} handleQuantity={handleQuantity}/>
            </div>
            <Navbar/>
            <Countbar totalNum={totalNum} totalPrice={totalPrice}/>
        </div>
    )
}
