import coverImageUrl from "../img/bg.jpg";
import CartBook from "./CartBook";
import {useState} from "react";


export default function CartBookList({cartbook,SetCartbook}){

    const [totalPrice,setTotalPrice] = useState(0);
    const handleCheck = (id,isChecked) => {
        const newCartbook = cartbook.map(book =>
            book.id === id ? { ...book, checked: isChecked } : book
        );
        setCartbook(newCartbook);
        handlePrice();
    }

    const handleQuantity = (id,quantity) => {
        const newCartbook = cartbook.map(book =>
            book.id === id ? { ...book, quantity: quantity } : book
        );
        setCartbook(newCartbook);
        handlePrice();
    }

    const handlePrice = () => {
        let total = 0;
        cartbook.map(book => {
            if (book.checked) {
                total += book.price * book.quantity;
            }
        });
        setTotalPrice(total);
    }

    return(
        <div>
            <div className="booklist flex flex-col gap-4">
                {cartbook.map(book => (
                    <CartBook
                        key={book.id}
                        id={book.id}
                        coverImageUrl={book.coverImageUrl}
                        title={book.title}
                        author={book.author}
                        price={book.price}
                        checked={book.checked}
                        onCheck={handleCheck}
                        quantity={book.quantity}
                        onQuantity={handleQuantity}
                    />
                ))}
            </div>
            <div className="w-full h-20 bg-gray-100"></div>
        </div>

    )
}
