import coverImageUrl from "../img/bg.jpg";
import CartBook from "./CartBook";
import {useState,useEffect} from "react";


export default function CartBookList({cartbook,setCartbook,setTotalPrice,setTotalNum}){


    const handleCheck = (id,isChecked) => {
        const newCartbook = cartbook.map(book =>
            book.id === id ? { ...book, checked: isChecked } : book
        );
        setCartbook(newCartbook);
    }

    const handleQuantity = (id,quantity) => {
        const newCartbook = cartbook.map(book =>
            book.id === id ? { ...book, quantity: quantity } : book
        );
        setCartbook(newCartbook);
    }

    useEffect(() => {
        // 这个函数会在cartbook状态更新后调用
        handleCount();
    }, [cartbook]); // 依赖数组里面包含了cartbook，意味着cartbook变化时会触发这个effect

    const handleCount = () => {
        let totalprice = 0;
        let totalnum=0;
        cartbook.map(book => {
            if (book.checked) {
                totalprice += book.price * book.quantity;
                totalnum+=book.quantity;
            }
        });
        setTotalPrice(totalprice);
        setTotalNum(totalnum)
    }


    return(
        <div>
            <div className="booklist flex flex-col gap-4">
                {cartbook.map(book => (
                    <CartBook
                        key={book.id}
                        id={book.id}
                        coverImageUrl={book.coverImageUrl}
                        title={book.title}
                        author={book.author}
                        price={book.price}
                        checked={book.checked}
                        onCheck={handleCheck}
                        quantity={book.quantity}
                        onQuantity={handleQuantity}
                    />
                ))}
            </div>
            <div className="w-full h-20 bg-gray-100"></div>
        </div>

    )
}
