import {UploadOutlined} from '@ant-design/icons';
import {Button, ConfigProvider, Upload, Flex, Image, Input, Form} from 'antd';
import Navbar from "../components/Navbar";
import BackButton from "../components/BackButton";
import {useState} from "react";

const {TextArea} = Input;

export default function ProfilePage({person, setPerson}) {
    const [tempPerson, setTempPerson] = useState(person);

    const handleSave = () => {
        setPerson(tempPerson);
    }

    const handleCancel = () => {
        setTempPerson(person);
    }

    return (
        <div className="absolute w-full top-24 px-16 bg-gray-100">
            <div className="absolute ml-56 mt-16">
                <p className="text-4xl">个人信息</p>
            </div>
            <Flex className="mt-28">
                <Flex vertical align="center" gap="large" className="ml-56 m-8 mt-20">
                    <Image
                        width={300}
                        height={300}
                        src={person.avatar}
                    />
                    <Upload>
                        <Button icon={<UploadOutlined/>} size="large" type="default">修改头像</Button>
                    </Upload>
                </Flex>
                <Flex vertical gap="large" labelFontSize="20" className="ml-36">
                    <ConfigProvider
                        theme={{
                            components: {
                                Form: {
                                    labelColor: 'rgba(0, 0, 0, 0.6)',
                                    labelFontSize: '18px',
                                },
                                Input: {
                                    inputFontSize: '16px',
                                },
                                Button: {
                                    defaultBorderColor: '#0958d9',
                                    defaultColor: '#0958d9',
                                    defaultHoverBg: '#0958d9',
                                    defaultHoverColor: '#ffffff',
                                    defaultActiveBg: '#0958d9',
                                    defaultActiveColor: '#ffffff',

                                },
                            },
                        }}>
                        <Form layout="vertical"
                              labelCol={{span: 10}}  // 设置标签的宽度占 8 个格
                              wrapperCol={{span: 30}}>
                            <Form.Item label="昵称">
                                <Input style={{width: 350}} value={tempPerson.name} size="large"
                                       onChange={e=>setTempPerson({...tempPerson, name: e.target.value})}/>
                            </Form.Item>

                            <Form.Item label="联系方式">
                                <Input style={{width: 350}} value={tempPerson.contact} size="large"
                                        onChange={e=>setTempPerson({...tempPerson, contact: e.target.value})}/>
                            </Form.Item>

                            <Form.Item label="个人简介">
                                <TextArea
                                    showCount
                                    maxLength={100}
                                    value={tempPerson.introduction}
                                    style={{
                                        width: 350,
                                        height: 100,
                                        resize: 'none',
                                    }}
                                    onChange={e => setTempPerson({
                                        ...tempPerson,
                                        introduction: e.target.value,
                                    })}
                                />
                            </Form.Item>

                            <Form.Item>
                                <Button className="" size="large" onClick={handleSave}>保存</Button>
                                <Button type="default" size="large" className="ml-8" onClick={handleCancel}>取消</Button>
                            </Form.Item>
                        </Form>
                    </ConfigProvider>


                </Flex>
            </Flex>

            <div className="w-full h-20 bg-gray-100"></div>
            <div className="fixed top-0 w-full h-20 bg-white flex flex-col items-center">
                <Navbar/>
            </div>

        </div>
    )
}
