import React from 'react';
import { useNavigate } from 'react-router-dom';
import avatar from '../img/bg.jpg';
import type { MenuProps} from 'antd';
import { Button, Dropdown, Space } from 'antd';

const items: MenuProps['items'] = [
    {
        key: '1',
        label: (
            <a target="_blank" rel="noopener noreferrer" href="https://www.antgroup.com">
                1st menu item
            </a>
        ),
    },
    {
        key: '2',
        label: (
            <a target="_blank" rel="noopener noreferrer" href="https://www.aliyun.com">
                2nd menu item
            </a>
        ),
    },
    {
        key: '3',
        label: (
            <a target="_blank" rel="noopener noreferrer" href="https://www.luohanacademy.com">
                3rd menu item
            </a>
        ),
    },
];

function ProfileButton() {
    const navigate = useNavigate();
    const handleClick = () => {
        navigate('/profile'); // 点击按钮时改变路由地址
    }
    return (
        <Dropdown menu={{items}} placement="bottomRight" arrow>
            <button className="flex absolute right-0  mr-16 mt-4 " onClick={handleClick}>
                < img src={avatar} alt="user"
                      className="h-10 w-10 mr-6 rounded-full hover:shadow-lg transform hover:scale-105" />
            < /button>
        </Dropdown>

    );
}

export default ProfileButton;
