import Navbar from "../components/Navbar";
import {useNavigate,useParams} from "react-router-dom";
import BackButton from "../components/BackButton";
import BookDetail from "../components/BookDetail";

export default function BookDetailPage(books) {
    let { id } = useParams(); // 获取URL参数
    id = parseInt(id, 10); // 确保id是数字类型
    const book = books.books.find(book => book.id === id); // 通过id查找对应的book

    return(
        <div>
            <div className="relative flex flex-col w-full bg-gray-100">
                <BookDetail book={book}/>
                <BackButton/>
            </div>
            <Navbar/>
        </div>
    )

}
