import {UploadOutlined} from '@ant-design/icons';
import {Button, ConfigProvider, Upload, Flex, Image, Input, Form} from 'antd';
import Navbar from "../components/Navbar";

const {TextArea} = Input;

export default function ProfilePage({person, setPerson}) {
    return (
        <div className="absolute w-full top-24 px-16 bg-gray-100">
            <Flex className="mt-28">
                <Flex vertical align="center" gap="large" className="ml-56">
                    <Image
                        width={300}
                        height={300}
                        src={person.avatar}
                    />
                    <Upload>
                        <Button icon={<UploadOutlined/>} size="large" type="default">修改头像</Button>
                    </Upload>
                </Flex>
                <Flex vertical gap="large" labelFontSize="20" className="ml-40">
                    <ConfigProvider
                        theme={{
                            components: {
                                Form: {
                                    labelFontSize: '18px',
                                },
                                Input: {
                                    inputFontSize: '18px',
                                },
                            },
                        }}>
                        <Form labelCol={{span: 8}}  // 设置标签的宽度占 8 个格
                              wrapperCol={{span: 30}}>
                            <Form.Item label="昵称">
                                <Input value={person.name}/>
                            </Form.Item>

                            <Form.Item label="联系方式">
                                <Input style={{
                                    height: 100,
                                }}
                                       value={person.contact}/>
                            </Form.Item>

                            <Form.Item label="个人简介">
                                <TextArea
                                    showCount
                                    maxLength={100}

                                    value={person.introduction}
                                    style={{
                                        height: 100,
                                        resize: 'none',
                                    }}
                                />
                            </Form.Item>
                        </Form>
                    </ConfigProvider>


                </Flex>
            </Flex>

            <div className="fixed top-0 w-full h-20 bg-white flex flex-col items-center">
                <Navbar/>
            </div>
        </div>
    )
}
