import { BrowserRouter, Route, Routes } from "react-router-dom";
import HomePage from "../page/home";
import CartPage from "../page/cart";
import BookDetailPage from "../page/bookdetail";
import ProfilePage from "../page/profile";
import { useState } from "react";
import Navbar from "./Navbar";

export default function AppRouter({book, setBook,person,setPerson}) {
    const [cartbook, setCartbook] = useState([]);
    //console.log(book);
    const handleAdd = (addBook) => {
        const isExist = cartbook.some(book => book.id === addBook.id);
        if (isExist) {
            const NewCartbook = cartbook.map(book =>
                book.id === addBook.id ? {...book, quantity: parseInt(book.quantity) + 1} : book
            );
            setCartbook(NewCartbook);
        } else
            setCartbook([...cartbook, addBook]);
    }

    const [page,setPage]=useState(1);
    const handlePageChange = (newPage) => {
        if(newPage>=1&&((newPage-1)*12<book.length)){
            setPage(newPage);
            return 1;
        }else{
            return 0;
        }
    }

    return (
        <BrowserRouter>
            <Routes>
                <Route index element={<HomePage book={book} setBook={setBook} page={page} handlePageChange={handlePageChange}/>}/>
                <Route path="/cart"
                       element={<CartPage cartbook={cartbook} setCartbook={setCartbook}/>}/>
                <Route path="/profile" element={<ProfilePage person={person} setPerson={setPerson}/>}/>
                <Route path="/book/:id" element={<BookDetailPage books={book} handleAdd={handleAdd}/>}/>
            </Routes>
            <div className="fixed top-0 w-full h-20 bg-white flex flex-col items-center">
                <Navbar person={person}/>
            </div>
        </BrowserRouter>
    );
}
