import trash from '../img/trash.png';
import {useNavigate} from "react-router-dom";

export default function OrderBook({order}) {
    console.log(order);
    const navigate = useNavigate();
    const handle2detail = (id) => {
        navigate('/book/' + id);
    }
    return (
        <div className="flex flex-col items-center bg-white rounded-lg mt-8 mx-8  hover:shadow-lg">
            <div className="flex flex-row justify-between w-full h-12 bg-blue-800 rounded-t-lg">
                <p className="ml-8 text-2xl">{order.data}</p>
                <p className="mr-8 text-2xl">{order.status}</p>
                <p className="mr-8 text-2xl">{'짜' + order.total}</p>
            </div>
            {order.book.map(book => (
                <div className="flex flex-row items-center">
                    <img src={`http://localhost:8080/images/pc.png`} className="ml-32 h-32 rounded-lg"/>
                    <div className="flex flex-col ml-48 text-left h-32">
                    <button className="text-2xl mt-8 text-left hover:text-blue-900"
                                onClick={handle2detail}>{book.title}</button>
                        <p className="mt-4 text-xs text-gray-400">{book.author}</p>
                    </div>
                    <p className="ml-56 text-xl text-blue-800">{'짜' + book.price}</p>
                    <input disabled
                           type="number"
                           className="w-12 h-10 ml-56 border-2 border-blue-400 rounded-lg text-center"
                           value={book.quantity}
                    />
                </div>
            ))}
        </div>


    );
}
