import { StyleSheet } from 'react-native';

export const styles = StyleSheet.create({
    cartprice: {
        fontSize: 24,  // 大约24像素
        color: '#1e40af',  // 深蓝色
        paddingRight: 20,  // 右侧内边距
        marginRight: 20,  // 右侧外边距
        paddingVertical: 8,  // 上下内边距
        marginVertical: 4,  // 上下外边距
        borderColor: '#d1d5db',  // 边框颜色
        borderWidth: 1,  // 边框宽度
        backgroundColor: '#f3f4f6'  // 背景色
    }
});
