import trash from '../img/trash.png';
import {useNavigate} from "react-router-dom";
import Table from "./Table";

export default function OrderBook({order}) {
    const columns = [
        { header: 'Title', accessor: 'title' },
        { header: 'Price', accessor: 'price' },
        { header: 'Quantity', accessor: 'quantity' },
    ];
    console.log(order);
    const navigate = useNavigate();
    const handle2detail = (id) => {
        navigate('/book/' + id);
    }
    return (
        <div className="flex flex-col items-center bg-white rounded-lg mt-8 mx-8  hover:shadow-lg">
            <div className="flex flex-row mt-8">
                <p className="ml-8 text-2xl">{order.data}</p>
                <p className="mr-8 text-2xl">{order.status}</p>
                <p className="mr-8 text-2xl">{'¥' + order.total}</p>
            </div>
            <Table data={order.book} columns={columns} />
        </div>


    );
}
