import { UploadOutlined } from '@ant-design/icons';
import {Button, Upload,Flex, Image, Input, Form} from 'antd';
import Navbar from "../components/Navbar";

export default function ProfilePage({person, setPerson}) {
    return (
        <div className="absolute w-full top-24 px-16 bg-gray-100">
            <Flex className="mt-28">
                <Flex vertical gap="large" className="ml-40">
                    <Image
                        width={300}
                        height={300}
                        src={person.avatar}
                    />

                </Flex>
                <Flex vertical gap="large">
                    <Form labelCol={{ span: 8 }}
                          wrapperCol={{ span: 16 }} >
                        <Form.Item label="昵称">
                            <Input value={person.name} onChange={(e) => setPerson({...person, name: e.target.value})}/>
                        </Form.Item>

                        <Form.Item label="联系方式">
                            <Input value={person.contact}
                                   onChange={(e) => setPerson({...person, contact: e.target.value})}/>
                        </Form.Item>

                        <Form.Item label="个人简介">
                            <Input value={person.introduction}
                                   onChange={(e) => setPerson({...person, introduction: e.target.value})}/>
                        </Form.Item>
                    </Form>

                </Flex>
            </Flex>

            <div className="fixed top-0 w-full h-20 bg-white flex flex-col items-center">
                <Navbar/>
            </div>
        </div>
    )
}
